CREATE TABLE herbalife_dev.ci_usuarios
(
    id_usuario int(11) unsigned PRIMARY KEY NOT NULL AUTO_INCREMENT,
    nombre varchar(256),
    apellido varchar(256),
    username varchar(250),
    email varchar(100) DEFAULT '' NOT NULL COMMENT '{"validate":["email"]}',
    password varchar(128) DEFAULT '' NOT NULL COMMENT '{"validate":["password"],"input":"password"}',
    fec_nacimiento date,
    sexo varchar(15) COMMENT '{"input":"radios","options":["Masculino","Femenino"]}',
    invitado_por int(10) unsigned COMMENT '{"label":"Invitado por","input":"dropdown","selectBy":["nombre","apellido"],"validate":0}',
    phone_number_1 varchar(20) COMMENT '{"label":"Telefono 1"}',
    phone_number_2 varchar(20) COMMENT '{"labe":"Telefono 2"}',
    cellphone_number_1 varchar(20) COMMENT '{"label":"Celular 1"}',
    cellphone_number_2 varchar(20) COMMENT '{"label":"Celular 2"}',
    cod_acceso varchar(50) COMMENT '{"label":"Codigo de acceso"}',
    id_option_tipo_asociado int(10) unsigned COMMENT '{"label":"Tipo de asociado","input":"dropdown","selectBy":["id_setting"],"filterBy":{"id_setting":2,"0":"nombre"}}',
    id_option_nivel_asociado int(10) unsigned COMMENT '{"label":"Nivel de asociado","input":"dropdown","selectBy":["id_setting"],"filterBy":{"id_setting":3,"0":"nombre"}}',
    id_club int(10) unsigned COMMENT '{"label":"Club Perteneciente","input":"select","selectBy":["nombre"]}',
    id_turno int(10) unsigned COMMENT '{"label":"Turno de","input":"dropdown","selectBy":"id_asociado"}',
    id_sesion int(10) unsigned COMMENT '{"label":"Sesion de","input":"select","selectBy":["id_asociado"]}',
    id_opcion_role int(10) unsigned COMMENT '{"label":"Role del usuario","input":"radios","selectBy":["id_setting"],"filterBy":{"id_setting":4,"0":"nombre"}}',
    foto_perfil int(11) unsigned COMMENT '{"label":"Foto de perfil","input":"image","validate":0}',
    app_monitor varchar(50) COMMENT '{"label":"Permitir app monitoreo","input":"radios","options":["SI","NO"]}',
    app_orders varchar(50) COMMENT '{"label":"Permitir app ordenes","input":"radios","options":["SI","NO"]}',
    app_admin varchar(50) COMMENT '{"label":"Permitir app administrador","input":"radios","options":["SI","NO"]}',
